  } catch (e) {
    if (e instanceof NotFoundError || (e as Error).name === 'NotFoundError') {
      return res.status(404).json({ error: (e as Error).message })
    }

    console.error('Error in getUserById endpoint', (e as Error).message)
    res
      .status(500)
      .json({ error: (e as Error).message || 'Internal server error' })
  }
}

const createUserEP = async (req: Request, res: Response) => {
  const { name, password } = req.body

  if (!name || !password) {
    res.status(400).json({ error: 'Name and password are required' })
  }

  try {
    const user = await createUser(name, password)

    if (!user) {
      return res.status(400).json({ error: 'Failed to create user' })
    }

    res.status(201).json(user)
  } catch (e: any) {
    res.status(500).json({ error: e.message || 'Internal server error' })
  }
}

const getAllUsersEP = async (req: Request, res: Response) => {
  try {
    const users = await getAllUsers()
    res.status(200).json(users)
  } catch (e: any) {
    res.status(500).json({ error: e.message || 'Internal server error' })
  }
}

export default {
  getUserByIdEP,
  createUserEP,
  getAllUsersEP,
}

